mapper.insert:
insert
  into member
  	 (
       user_id
     , user_pwd
     , user_name
     , email
     , gender
     , age
     , phone
     , address
     )
values
	 (
	   #{userId}
	 , #{userPwd}
	 , #{userName}
	 , #{email}
	 , #{gender}
	 , #{age}
	 , #{phone}
	 , #{address}
	 )

mapper.update:
	update member
	   set user_name = #{userName}
	     , email = #{email}
	     , age = #{age}
	     , phone = #{phone}
	     , address = #{address}
	     , gender = #{gender}
	     , modify_date = sysdate
 	 where user_id = #{userId}

update member
   set status = 'N'
 where user_id = #{userId}
